begin
wwv_flow_api.create_plugin(
 p_id=>2011920213809762287+wwv_flow_api.g_id_offset
,p_plugin_type=>'ITEM TYPE'
,p_name=>'COM.NESCO.APEX.PLUGINS.MULTISELECT_CBLIST'
,p_display_name=>'Multi-Select-Checkbox-List'
,p_supported_ui_types=>'DESKTOP'
,p_plsql_code=>wwv_flow_utilities.join(wwv_flow_t_varchar2(
'-- renders a multi-selectlist with checkboxes and "All"-Selector.',
'-- All html-object styles are customizable within the apex builder.',
'-- NOTE!',
'-- 1. Return-values will be substituted in an apex item. Please be aware that the length of substituted return-values should ',
'--    be greater than 4000 characters. Otherwise you got an rendering error!',
'-- 2. For a correct "All"-Selector handling it was necessary to use a special value ("-9") and set it at the beginning',
'--    of the return string. So if you check your return value you''ll find the value "-9" if you''ve checked "All".',
'-- 3. Setting Default-Values is not supported! But you can enable the "All"-selector as default (in Plugin-Settings section).',
'function render_multi_select_cb_list(',
'    p_item                in apex_plugin.t_page_item,',
'    p_plugin              in apex_plugin.t_plugin,',
'    p_value               in varchar2,',
'    p_is_readonly         in boolean,',
'    p_is_printer_friendly in boolean )',
'    return apex_plugin.t_page_item_render_result',
'is',
'  SUBTYPE LONG_VARCHAR2       IS VARCHAR2(32767);',
'  ',
'  c_listval_delimiter         constant varchar2(4000) := '':'';',
'  c_enable_all_default_string varchar2(4000) := p_item.attribute_01;',
'  c_fieldset_style_string     varchar2(4000) := case when p_item.attribute_02 is null then null else '' style="''||p_item.attribute_02||''"'' end;',
'  c_allcb_field_style_string  varchar2(4000) := case when p_item.attribute_03 is null then null else '' style="''||p_item.attribute_03||''"'' end;',
'  c_allcb_label_style_string  varchar2(4000) := case when p_item.attribute_04 is null then null else '' style="''||p_item.attribute_04||''"'' end;',
'  c_div_style_string          varchar2(4000) := case when p_item.attribute_05 is null then null else '' style="''||p_item.attribute_05||''"'' end;',
'  c_cblist_field_style_string varchar2(4000) := case when p_item.attribute_06 is null then null else '' style="''||p_item.attribute_06||''"'' end;',
'  c_cblist_label_style_string varchar2(4000) := case when p_item.attribute_07 is null then null else '' style="''||p_item.attribute_07||''"'' end;',
'  c_add_label_style_string    varchar2(4000) := case when p_item.attribute_09 is null then null else '' style="''||p_item.attribute_09||''"'' end;',
'  c_enable_presearch_option   varchar2(4000) := p_item.attribute_10;',
'',
'  l_additional_label_between  varchar2(100)  := p_item.attribute_08;',
'  ',
'  -- constants for accessing our l_column_value_list array',
'  c_display_column constant number := 1;',
'  c_return_column  constant number := 2;',
'  c_all_selected   constant varchar2(10) := ''-9'';',
'',
'  -- value without the lov null value',
'  l_value             LONG_VARCHAR2 := case when p_value = p_item.lov_null_value then null else p_value end;',
'  l_name              varchar2(30);',
'',
'  l_checked_string    varchar2(50) := NULL;',
'  l_value_found       boolean := false;',
'  l_all_found         boolean := false;',
'  l_display_value     LONG_VARCHAR2;',
'  l_is_selected       boolean := false;',
'  l_column_value_list apex_plugin_util.t_column_value_list;',
'  l_result            apex_plugin.t_page_item_render_result := NULL;',
'',
'  function isPreSearchOptionEnabled',
'           return BOOLEAN',
'  is',
'  begin',
'    IF NVL(c_enable_presearch_option, ''N'') = ''Y'' THEN',
'      RETURN TRUE;',
'    ELSE',
'      RETURN FALSE;',
'    END IF;',
'  end isPreSearchOptionEnabled;',
'  ',
'  function init_retval',
'    return LONG_VARCHAR2',
'  is',
'    l_retval  LONG_VARCHAR2;',
'  begin',
'    l_retval := c_all_selected;',
'    ',
'    -- loop through resultset',
'    for i in 1 .. l_column_value_list(c_return_column).count',
'    loop',
'      if l_retval is not null then',
'        l_retval := l_retval || c_listval_delimiter;',
'      end if;',
'      l_retval := l_retval || l_column_value_list(c_return_column)(i);',
'    end loop;',
'    ',
'    return l_retval;',
'  end init_retval;',
'  ',
'  function is_value_selected(',
'      p_display_value       in varchar2,',
'      p_return_value        in varchar2,',
'      p_compare_value_list  in varchar2 )',
'    return boolean',
'  is',
'  begin',
'    if l_all_found',
'      or p_compare_value_list = p_return_value',
'      or p_compare_value_list like p_return_value||c_listval_delimiter||''%''',
'      or p_compare_value_list like ''%''||c_listval_delimiter||p_return_value||c_listval_delimiter||''%''',
'      or p_compare_value_list like ''%''||c_listval_delimiter||p_return_value',
'    then',
'      return true;',
'    end if;',
'    ',
'    return false;',
'  end is_value_selected;',
'',
'  procedure print_cb_option (',
'      p_item_name           in varchar2,',
'      p_display_value       in varchar2,',
'      p_return_value        in varchar2,',
'      p_compare_value_list  in varchar2 )',
'  is',
'    l_is_selected boolean := is_value_selected(p_display_value, p_return_value, p_compare_value_list);',
'  begin',
'    if not l_value_found and l_is_selected',
'    then',
'      l_value_found := true;',
'    end if;',
'    if l_is_selected',
'    then',
'      l_checked_string := '' checked="checked"'';',
'    else',
'      l_checked_string := NULL;',
'    end if;',
'    htp.prn(''<div class="option"><input type="checkbox" value="''||p_return_value||''"''||l_checked_string||c_cblist_field_style_string||'' /><label''||c_cblist_label_style_string||''>''||p_display_value||''</label></div>'');',
'  end print_cb_option;',
'begin',
'',
'  -- During plug-in development it''s very helpful to have some debug information',
'  if wwv_flow.g_debug then',
'      apex_plugin_util.debug_page_item (',
'          p_plugin              => p_plugin,',
'          p_page_item           => p_item,',
'          p_value               => p_value,',
'          p_is_readonly         => FALSE,',
'          p_is_printer_friendly => FALSE);',
'  end if;',
'',
'  apex_css.add_file (',
'      p_name      => ''multi_select_checkbox_list'',',
'      p_directory => p_plugin.file_prefix,',
'      p_version   => null );',
'  apex_javascript.add_library (',
'      p_name      => ''multi_select_checkbox_list'',',
'      p_directory => p_plugin.file_prefix,',
'      p_version   => null );',
'',
'  -- get all values',
'  l_column_value_list := apex_plugin_util.get_data (',
'                             p_sql_statement      => p_item.lov_definition,',
'                             p_min_columns        => 2,',
'                             p_max_columns        => 2,',
'                             p_component_name     => p_item.name );',
'',
'',
'  -- check if used for the first time',
'  if p_value is null and NVL(c_enable_all_default_string, ''N'') = ''Y'' then',
'    l_value := init_retval;',
'  end if;',
'   ',
'  -- check if "All" is selected',
'  if substr(l_value, 1, 2) = c_all_selected',
'  then',
'    l_all_found := true;',
'  end if;',
'',
'  -- if "All" found mark as "checked"',
'  if l_all_found then',
'    l_checked_string := '' checked="checked"'';',
'  else',
'    l_checked_string := NULL;',
'  end if;',
'',
'  htp.prn(''<fieldset class="multi_checkbox_group" id="fs_''||p_item.name||''"''||c_fieldset_style_string||''>'');',
'  l_name := apex_plugin.get_input_name_for_page_item(false);',
'  htp.prn(''  <input type="hidden" name="''||l_name||''" id="''||p_item.name||''" value="''||l_value||''" />'');',
'  htp.prn(''  <input type="checkbox"''||l_checked_string||'' value="''||c_all_selected||''" class="cball_''||p_item.name||''" onclick="handleOnClickCBAll(''''''||p_item.name||'''''');"''||c_allcb_field_style_string||'' /><label''||c_allcb_label_style_string||''>Alle<'
||'/label>'');',
'  if l_additional_label_between is not null then',
'    htp.prn(''  <br /><span class="multi_checkbox_add_label"''||c_add_label_style_string||''>''||l_additional_label_between||''</span>'');',
'  end if;',
'  if isPreSearchOptionEnabled  then',
'    htp.prn(''  <br />'');',
'    -- htp.prn(''  <input type="text" name="psip_''||p_item.name||''" id="psip_''||p_item.name||''" class="multi_checkbox_preser_text" />'');',
'    htp.prn(''  <input type="text" id="psip_''||p_item.name||''" class="multi_checkbox_preser_text" />'');',
'  end if;',
'  htp.prn(''  <div class="multi_checkbox_div" id="msl_''||p_item.name||''" onclick="handleOnClickList(''''''||p_item.name||'''''');"''||c_div_style_string||''>'');',
'',
'  -- add display null entry',
'  if p_item.lov_display_null then',
'      wwv_flow_utilities.add_null_value_entry(''ITEM'', l_name, p_item.lov_null_value);',
'      -- add list entry',
'      print_cb_option (',
'              p_item_name=>p_item.name,',
'              p_display_value => p_item.lov_null_text,',
'              p_return_value  => p_item.lov_null_value,',
'              p_compare_value_list => nvl(l_value, p_item.lov_null_value) );',
'  end if;',
'',
'  -- loop through resultset',
'  for i in 1 .. l_column_value_list(c_display_column).count',
'  loop',
'    print_cb_option',
'        (p_item_name=>p_item.name',
'        ,p_display_value=>l_column_value_list(c_display_column)(i)',
'        ,p_return_value=>l_column_value_list(c_return_column)(i)',
'        ,p_compare_value_list=>l_value);',
'  end loop;',
'',
'  -- Show at least the value if it hasn''t been found in the database',
'  if not l_value_found and l_value is not null and p_item.lov_display_extra then',
'      print_cb_option (',
'          p_item_name=>p_item.name,',
'          p_display_value => l_value,',
'          p_return_value  => l_value,',
'          p_compare_value_list => l_value );',
'  end if;',
'',
'  htp.prn(''  </div>'');',
'  htp.prn(''</fieldset>'');',
'',
'  if wwv_flow.g_debug then',
'    htp.prn(''<button type="button" id="btn" onclick="get_checkbox_values(''''''||p_item.name||'''''');">Get checkbox values</button>'');',
'  end if;',
'',
'  apex_javascript.add_onload_code (',
'    p_code => ''multiSelectListCBHandler(''||',
'                  ''"#''||p_item.name||''",''||',
'                  ''{''||',
'                  apex_javascript.add_attribute(''ajaxIdentifier'',      apex_plugin.get_ajax_identifier)||',
'                  apex_javascript.add_attribute(''dependingOnSelector'', apex_plugin_util.page_item_names_to_jquery(p_item.lov_cascade_parent_items))||',
'                  apex_javascript.add_attribute(''optimizeRefresh'',     p_item.ajax_optimize_refresh)||',
'                  apex_javascript.add_attribute(''pageItemsToSubmit'',   apex_plugin_util.page_item_names_to_jquery(p_item.ajax_items_to_submit))||',
'                  apex_javascript.add_attribute(''nullValue'',           p_item.lov_null_value, false, false)||',
'                  ''});''',
'                  );',
'  if isPreSearchOptionEnabled then',
'    apex_javascript.add_onload_code (',
'      p_code => ''$(".multi_checkbox_preser_text").keyup( function() { ''||',
'                    ''  handleKeyUpPreSearch(this); ''||',
'                    ''}); ''',
'                    );',
'  end if;',
'  ',
'  l_result.is_navigable := true;',
'',
'  return l_result;',
'end render_multi_select_cb_list;',
'',
'function ajax_refresh_list (',
'    p_item   in apex_plugin.t_page_item,',
'    p_plugin in apex_plugin.t_plugin )',
'    return apex_plugin.t_page_item_ajax_result',
'IS',
'  l_result            apex_plugin.t_page_item_ajax_result := NULL;',
'BEGIN',
'',
'  APEX_PLUGIN_UTIL.PRINT_LOV_AS_JSON',
'         (p_sql_statement=>p_item.lov_definition',
'         ,p_component_name=>p_item.name',
'         ,p_escape=>TRUE);',
'',
'  return l_result;',
'',
'END ajax_refresh_list;',
''))
,p_render_function=>'render_multi_select_cb_list'
,p_ajax_function=>'ajax_refresh_list'
,p_standard_attributes=>'VISIBLE:SESSION_STATE:SOURCE:ELEMENT:WIDTH:HEIGHT:ELEMENT_OPTION:LOV:LOV_REQUIRED:CASCADING_LOV'
,p_sql_min_column_count=>2
,p_sql_max_column_count=>2
