begin
wwv_flow_api.create_plugin(
 p_id=>2019931038215208908+wwv_flow_api.g_id_offset
,p_plugin_type=>'ITEM TYPE'
,p_name=>'APEX.VCPLUGIN.COMBOBOX'
,p_display_name=>'Combobox'
,p_supported_ui_types=>'DESKTOP'
,p_plsql_code=>wwv_flow_utilities.join(wwv_flow_t_varchar2(
'function render_combobox (',
'    p_item                in apex_plugin.t_page_item,',
'    p_plugin              in apex_plugin.t_plugin,',
'    p_value               in varchar2,',
'    p_is_readonly         in boolean,',
'    p_is_printer_friendly in boolean )',
'    return apex_plugin.t_page_item_render_result',
'is    ',
'    l_value             varchar2(32767) := p_value;',
'    l_name              varchar2(30);',
'    l_display_value     varchar2(32767);',
'    l_all_values  apex_plugin_util.t_column_value_list;',
'    l_result            apex_plugin.t_page_item_render_result;',
'',
'begin',
'',
'-- little customized version of jquery autocomplete js',
'apex_css.add_file(',
'    p_name      => ''apex.vcplugin-ac'',',
'    p_directory => p_plugin.file_prefix,',
'    p_version   => null);',
'apex_javascript.add_library (',
'    p_name      => ''jquery.ui.core.min'',',
'    p_directory => ''#IMAGE_PREFIX#libraries/jquery-ui/1.8/ui/minified/'',',
'    p_version   => null);',
'apex_javascript.add_library (',
'    p_name      => ''jquery.ui.widget.min'',',
'    p_directory => ''#IMAGE_PREFIX#libraries/jquery-ui/1.8/ui/minified/'',',
'    p_version   => null);',
'apex_javascript.add_library (',
'    p_name      => ''jquery.ui.button.min'',',
'    p_directory => ''#IMAGE_PREFIX#libraries/jquery-ui/1.8/ui/minified/'',',
'    p_version   => null);',
'apex_javascript.add_library (',
'    p_name      => ''jquery.ui.position.min'',',
'    p_directory => ''#IMAGE_PREFIX#libraries/jquery-ui/1.8/ui/minified/'',',
'    p_version   => null);',
'apex_css.add_file(',
'    p_name      => ''apex.vcplugin-custom'',',
'    p_directory => p_plugin.file_prefix,',
'    p_version   => null);',
'apex_javascript.add_library (',
'    p_name      => ''apex.vcplugin-ac.min'',',
'    p_directory => p_plugin.file_prefix,',
'    p_version   => null);',
'apex_javascript.add_library (',
'    p_name      => ''apex.vcplugin-widget.min'',',
'    p_directory => p_plugin.file_prefix,',
'    p_version   => null);',
'',
' if wwv_flow.g_debug then',
'  apex_plugin_util.debug_page_item (',
'   p_plugin              => p_plugin,',
'   p_page_item           => p_item,',
'   p_value               => p_value,',
'   p_is_readonly         => p_is_readonly,',
'   p_is_printer_friendly => p_is_printer_friendly );',
' end if;',
'',
'    if p_is_readonly or p_is_printer_friendly then',
'              -- if item is set to readonly or running the page in printer friendly mode',
'        apex_plugin_util.print_hidden_if_readonly (',
'            p_item_name           => p_item.name,',
'            p_value               => p_value,',
'            p_is_readonly         => p_is_readonly,',
'            p_is_printer_friendly => p_is_printer_friendly );',
'',
'        l_display_value := apex_plugin_util.get_display_data (',
'                               p_sql_statement     => p_item.lov_definition,',
'                               p_min_columns       => 2,',
'                               p_max_columns       => 2,',
'                               p_component_name    => p_item.name,',
'                               p_display_column_no => 1,',
'                               p_search_column_no  => 2,',
'                               p_search_string     => l_value,',
'                               p_display_extra     => FALSE );',
'',
'        -- render display span with the value',
'        apex_plugin_util.print_display_only (',
'            p_item_name        => p_item.name,',
'            p_display_value    => l_display_value,',
'            p_show_line_breaks => false,',
'            p_escape           => true,',
'            p_attributes       => p_item.element_attributes );',
'    else',
'        -- Get internal name of the input element',
'        l_name := apex_plugin.get_input_name_for_page_item(false);',
'',
'      if nvl(p_item.attribute_01,''N'') = ''N'' then',
'         -- check if plugin is lazyload, then print local json data',
'        htp.p(''<script>'');',
'         htp.prn(''var ''||p_item.name||''_VCPLGN = ['');',
'        -- get all values limited to 500',
'        l_all_values := apex_plugin_util.get_data (',
'                                   p_sql_statement      => p_item.lov_definition,',
'                                   p_min_columns        => 2,',
'                                   p_max_columns        => 2,',
'                                   p_component_name     => p_item.name,',
'                                   p_max_rows           => 500);',
'        -- print json',
'          for i in 1 .. l_all_values(1).count loop',
'            htp.prn(''{ label: "'' || SYS.HTF.ESCAPE_SC(l_all_values(1)(i))',
'             || ''", value: "'' || SYS.HTF.ESCAPE_SC(l_all_values(1)(i))',
'             || ''", id: "'' || SYS.HTF.ESCAPE_SC(l_all_values(2)(i)) || ''" }'' || case when',
'              i <> l_all_values(1).count then '','' end);',
'          end loop;',
'         htp.p('']'');',
'         htp.p(''</script>'');',
'      end if;',
'-- print apex item which will be transformed into combobox through custom widget',
'   sys.htp.prn(''<input type="text" name="''||l_name||''" id="''||p_item.name||''" size="''||p_item.element_width||''" value="''||sys.htf.escape_sc(p_value)||''" ''||p_item.element_attributes||'' >'');',
'       if l_value is not null then',
'        l_display_value := apex_plugin_util.get_display_data (',
'                               p_sql_statement     => p_item.lov_definition,',
'                               p_min_columns       => 2,',
'                               p_max_columns       => 2,',
'                               p_component_name    => p_item.name,',
'                               p_display_column_no => 1,',
'                               p_search_column_no  => 2,',
'                               p_search_string     => l_value,',
'                               p_display_extra     => FALSE );',
'       end if;',
'        l_result.is_navigable := true;',
'-- add onload code that builds the combobox',
'apex_javascript.add_onload_code (',
'    p_code => ''$("#''||p_item.name||''").vcCombobox({lazyLoad:''||',
'    		case when nvl(p_item.attribute_01,''N'')=''Y'' then ''true'' else ''false'' end||',
'    		'', showAddNew:''||case when nvl(p_item.attribute_02,''N'')=''Y'' then ''true'' else ''false'' end||',
'    		'', displayValue:''''''||SYS.HTF.ESCAPE_SC(apex_javascript.escape(l_display_value))||''''''''||',
'    		'',acProc:''''''||apex_plugin.get_ajax_identifier||''''''});''',
'    );',
'    end if;',
'    return l_result;',
'end render_combobox;',
'',
'---- ajax part,basically used to get the json data OR to add a new value based on the plugin settings',
'function ajax_action (',
'    p_item   in apex_plugin.t_page_item,',
'    p_plugin in apex_plugin.t_plugin )',
'    return apex_plugin.t_page_item_ajax_result',
'is',
'l_all_values apex_plugin_util.t_column_value_list;',
'l_new_val varchar2(32767);',
'l_display_value     varchar2(32767);',
'begin',
' if nvl(p_item.attribute_01,''N'') = ''Y'' and nvl(wwv_flow.g_x01,''X'') = ''ADDNEW'' then',
'     l_new_val := apex_plugin_util.get_plsql_function_result (p_plsql_function => p_item.attribute_03);',
'     htp.prn(l_new_val);',
'     wwv_flow.g_x01 :=null;',
' elsif nvl(p_item.attribute_01,''N'') = ''Y'' and nvl(wwv_flow.g_x01,''X'') = ''GETDISPLAY'' then',
'        l_display_value := apex_plugin_util.get_display_data (',
'                               p_sql_statement     => p_item.lov_definition,',
'                               p_min_columns       => 2,',
'                               p_max_columns       => 2,',
'                               p_component_name    => p_item.name,',
'                               p_display_column_no => 1,',
'                               p_search_column_no  => 2,',
'                               p_search_string     => wwv_flow.g_x02,',
'                               p_display_extra     => TRUE );',
'     wwv_flow.g_x01 :=null;',
'     wwv_flow.g_x02 :=null;',
'     htp.prn(l_display_value);',
' elsif nvl(p_item.attribute_01,''N'') = ''Y'' and nvl(wwv_flow.g_x01,''X'') <> ''ADDNEW'' then',
'  htp.prn(''['');',
'  -- get all values limited to 500',
'  if wwv_flow.g_x01 is not null then',
'      -- check the search string',
'        l_all_values := apex_plugin_util.get_data(',
'                                   p_sql_statement      => p_item.lov_definition,',
'                                   p_min_columns        => 2,',
'                                   p_max_columns        => 2,',
'                                   p_search_type      => apex_plugin_util.c_search_contains_ignore,',
'                                   p_search_column_no => 1,',
'                                   p_search_string    => apex_plugin_util.get_search_string (',
'                                                                      p_search_type   => apex_plugin_util.c_search_contains_ignore,',
'                                                                      p_search_string => wwv_flow.g_x01 ),',
'                                   p_component_name     => p_item.name,',
'                                   p_max_rows           => 500);',
'      wwv_flow.g_x01 := null;',
'   else',
'        l_all_values := apex_plugin_util.get_data(',
'                                   p_sql_statement      => p_item.lov_definition,',
'                                   p_min_columns        => 2,',
'                                   p_max_columns        => 2,',
'                                   p_component_name     => p_item.name,',
'                                   p_max_rows           => 500);',
'   end if;',
'  -- print json',
'   for i in 1 .. l_all_values(1).count loop',
'    htp.prn(''{ label: "'' || SYS.HTF.ESCAPE_SC(l_all_values(1)(i))',
'             || ''", value: "'' || SYS.HTF.ESCAPE_SC(l_all_values(1)(i))',
'             || ''", id: "'' || SYS.HTF.ESCAPE_SC(l_all_values(2)(i)) || ''" }'' || case when',
'            i <> l_all_values(1).count then '','' end);',
'        end loop;',
'  htp.p('']'');',
'  end if;',
'   return null;',
'exception when others then',
'    htp.p(sqlerrm);',
'    return null;',
'end ajax_action;'))
,p_render_function=>'render_combobox'
,p_ajax_function=>'ajax_action'
,p_standard_attributes=>'VISIBLE:READONLY:SOURCE:ELEMENT:WIDTH:LOV:LOV_REQUIRED'
,p_sql_min_column_count=>2
,p_sql_max_column_count=>2
,p_sql_examples=>wwv_flow_utilities.join(wwv_flow_t_varchar2(
'<pre>',
'<b>Example 1: List of values based on SQL query</b>',
'',
'SELECT ename, empno FROM emp ORDER by 1',
'',
'<b>Example 2: List of values with same display and return value</b>',
'',
'SELECT ename d, ename r FROM emp ORDER BY 1',
'',
'When selecting the same column for both the display and return values use a column alias.',
'',
'<b>Example 3: Static list examples</b>',
'',
'    STATIC:Cow,Dog,Cat,Lion (will be sorted alphabetically)',
'    STATIC2:10,15,20,25,50,100,200,500,1000,10000 (sorted in order of creation)',
'    STATIC:Yes;Y,No;N (show Yes / No, return Y and N)',
'',
'<b>Example 4: List of values with bind variable</b>',
'',
'SELECT ENAME, EMPNO FROM EMP WHERE DEPTNO = :P3_DEPTNO ORDER by 1',
'',
'</pre>'))
,p_substitute_attributes=>true
,p_subscribe_plugin_settings=>true
,p_help_text=>wwv_flow_utilities.join(wwv_flow_t_varchar2(
'<p>',
'	Combobox (searchable select list) is a great replacement to the standard select list and autocomplete. It removes the limitation of APEX autocomplete item which can have only one column for display and return. This is easy to configure and easy to u'
||'se that enables you to add a new value to the drop down list on the fly.</p>',
'<p>',
'	<strong><u>Comes with two options:</u></strong></p>',
'<p>',
'	<strong>Lazy Loading:&nbsp; </strong><span class="instructiontext">If set to yes, the matching values will be read from the database each time the user types a character. If set to no, all values will be read only once when the page is rendered.</sp'
||'an></p>',
'<p>',
'	<strong>Allow Add New: </strong>Chose yes if you want to allow the user to add new values to the list using below PL/SQL Block (Only Available if<span style="font-weight: bold;"> Lazy Loading</span><strong> </strong>is set to Yes)</p>',
'<p>',
'	<strong>PL/SQL Code for Add New: </strong>Enter the PL/SQL Function Body to populate the new value into database table returning its ID (Only Available if<span style="font-weight: bold;"> </span><strong>Allow Add New</strong><strong> </strong>is set'
||' to Yes)</p>',
'<p>',
'	&nbsp;</p>',
'<p>',
'	<u>Set Value of Combobox Using below syntax:</u></p>',
'<p>',
'	$(&#39;#PX_ITEM_NAME&#39;).vcCombobox(&#39;SetValue&#39;,&lt;&lt;VALUE&gt;&gt;);</p>',
'<p>',
'	<u>For Example:</u></p>',
'<p>',
'	$(&#39;#P4_TEST&#39;).vcCombobox(&#39;SetValue&#39;,9999);</p>',
''))
,p_version_identifier=>'1.0'
,p_about_url=>'apex.oracle.com/pls/apex/f?p=1983:18'
);
