,p_plsql_code=>wwv_flow_utilities.join(wwv_flow_t_varchar2(
'function nethead_filedrop_ext_render(',
'  p_item          in apex_plugin.t_page_item, ',
'  p_plugin        in apex_plugin.t_plugin, ',
'  p_value         in varchar2, ',
'  p_is_readonly   in boolean,',
'  p_is_printer_friendly in boolean) ',
'  return apex_plugin.t_page_item_render_result',
'is',
'  l_name varchar2(30);',
'  l_result apex_plugin.t_page_item_render_result;',
'  ',
'  l_description         VARCHAR2(200) := p_plugin.attribute_01;',
'  l_error               VARCHAR2(200) := p_plugin.attribute_02;',
'  l_items_to_submit     VARCHAR2(32767) := p_item.attribute_01;',
'  l_vc_arr2             apex_application_global.vc_arr2;',
'begin',
'    if apex_application.g_debug then',
'      apex_plugin_util.debug_page_item (',
'        p_plugin              => p_plugin,',
'        p_page_item           => p_item,',
'        p_value               => p_value,',
'        p_is_readonly         => p_is_readonly,',
'        p_is_printer_friendly => p_is_printer_friendly );',
'    end if;',
'    ',
'    l_vc_arr2 := apex_util.string_to_table(l_items_to_submit, '','');',
