'    );',
'    apex_javascript.add_library (',
'        p_name      => ''apex.filedrop'',',
'        p_directory => p_plugin.file_prefix,',
'        p_version   => null',
'    );',
'    ',
'    htp.p(''<div id="''||p_item.name|| ''" class="nethead_dropfield"><span>''|| l_description ||''</span><em>''|| l_error ||''</em></div>'');',
'    ',
'',
'    apex_javascript.add_onload_code (',
'        p_code => ''',
'          Nethead.Apex.Filedrop.init({',
'            item: "#''||p_item.name|| ''",',
'            save: "''|| apex_plugin.get_ajax_identifier|| ''",',
'            args: "''|| l_items_to_submit ||''".length > 0 ? "''|| l_items_to_submit ||''".split(",") : []',
